const mimes = {
	'js': 'application/x-javascript',
	'pdf': 'application/pdf',
	'mp4': 'video/mp4',
	'css': 'text/css',
	'htm': 'text/html',
	'html': 'text/html',
	'txt': 'text/plain',
	'vcf': 'text/x-vcard',
	'xml': 'text/xml',
	'bmp': 'image/x-ms-bmp',
	'gif': 'image/gif',
	'jpeg': 'image/jpeg',
	'jpg': 'image/jpeg',
	'png': 'image/png',
}

class Mime {
	static get (ext) {
		return mimes[ext] || 'application/octet-stream'
	}
}

module.exports = Mime
